import React, { useEffect, useRef, useCallback } from "react";
import { Chart, registerables } from "chart.js";
import zoomPlugin from "chartjs-plugin-zoom";

Chart.register(...registerables, zoomPlugin);

const ChartComponent = ({ data }) => {
  const originalChartRef = useRef(null);
  const processedChartRef = useRef(null);
  const originalChartInstance = useRef(null);
  const processedChartInstance = useRef(null);
  const ZOOM_STEP = 1.25;
  const intersections = data?.intersections ?? [];

  const formatTime = (value) => {
    if (typeof value !== "number") return "-";
    return value.toExponential(3);
  };

  const formatSignal = (value) => {
    if (typeof value !== "number") return "-";
    return value.toFixed(5);
  };

  useEffect(() => {
    if (!data) return;

    const textColor = "#0f172a";
    const secondaryTextColor = "#475569";
    const gridColor = "rgba(148, 163, 184, 0.25)";
    const borderColor = "rgba(79, 70, 229, 0.35)";

    const buildOptions = (title, yLabel) => ({
      responsive: true,
      interaction: {
        mode: "index",
        intersect: false,
      },
      elements: {
        line: {
          borderJoinStyle: "round",
          borderCapStyle: "round",
        },
        point: {
          radius: 0,
          hitRadius: 10,
          hoverRadius: 5,
        },
      },
      scales: {
        x: {
          type: "linear",
          title: {
            display: true,
            text: "Время (секунды)",
            color: textColor,
            font: {
              size: 13,
              weight: "600",
            },
          },
          grid: {
            color: gridColor,
            zeroLineColor: borderColor,
          },
          border: {
            color: borderColor,
          },
          ticks: {
            color: secondaryTextColor,
            maxRotation: 0,
            padding: 6,
          },
        },
        y: {
          type: "linear",
          title: {
            display: true,
            text: yLabel,
            color: textColor,
            font: {
              size: 13,
              weight: "600",
            },
          },
          grid: {
            color: gridColor,
            zeroLineColor: borderColor,
          },
          border: {
            color: borderColor,
          },
          ticks: {
            color: secondaryTextColor,
            padding: 6,
          },
        },
      },
      plugins: {
        title: {
          display: true,
          text: title,
          color: textColor,
          font: {
            size: 18,
            weight: "600",
            family: "'Inter', 'Segoe UI', sans-serif",
          },
        },
        legend: {
          display: true,
          labels: {
            color: secondaryTextColor,
            usePointStyle: true,
            pointStyle: "circle",
            padding: 20,
            boxWidth: 12,
          },
        },
        tooltip: {
          backgroundColor: "rgba(15, 23, 42, 0.9)",
          titleFont: {
            weight: "600",
            size: 13,
          },
          bodyFont: {
            size: 12,
          },
          padding: 12,
          cornerRadius: 10,
          displayColors: true,
          callbacks: {
            labelColor: (context) => ({
              borderColor: context.dataset.borderColor,
              backgroundColor: context.dataset.borderColor,
            }),
          },
        },
        zoom: {
          pan: {
            enabled: true,
            mode: "xy",
          },
          zoom: {
            wheel: {
              enabled: true,
              modifierKey: "ctrl",
            },
            pinch: {
              enabled: true,
            },
            drag: {
              enabled: false,
            },
            mode: "xy",
          },
        },
      },
    });

    if (originalChartInstance.current) {
      originalChartInstance.current.destroy();
      originalChartInstance.current = null;
    }

    if (processedChartInstance.current) {
      processedChartInstance.current.destroy();
      processedChartInstance.current = null;
    }

    // График исходных данных
    if (originalChartRef.current) {
      const ctx = originalChartRef.current.getContext("2d");
      originalChartInstance.current = new Chart(ctx, {
        type: "line",
        data: {
          datasets: data.original,
        },
        options: buildOptions("Исходные сигналы", "Сигнал (В)"),
      });
    }

    // График обработанных данных
    if (processedChartRef.current) {
      const ctx = processedChartRef.current.getContext("2d");
      processedChartInstance.current = new Chart(ctx, {
        type: "line",
        data: {
          datasets: data.processed,
        },
        options: buildOptions("Обработанные сигналы", "Сигнал"),
      });
    }

    return () => {
      if (originalChartInstance.current) {
        originalChartInstance.current.destroy();
        originalChartInstance.current = null;
      }
      if (processedChartInstance.current) {
        processedChartInstance.current.destroy();
        processedChartInstance.current = null;
      }
    };
  }, [data]);

  const handleResetOriginal = useCallback(() => {
    originalChartInstance.current?.resetZoom();
  }, []);

  const handleResetProcessed = useCallback(() => {
    processedChartInstance.current?.resetZoom();
  }, []);

  const zoomChart = useCallback((chartRef, factor) => {
    chartRef.current?.zoom({ x: factor, y: factor });
  }, []);

  const handleZoomOriginalIn = useCallback(() => {
    zoomChart(originalChartInstance, ZOOM_STEP);
  }, [zoomChart, ZOOM_STEP]);

  const handleZoomOriginalOut = useCallback(() => {
    zoomChart(originalChartInstance, 1 / ZOOM_STEP);
  }, [zoomChart, ZOOM_STEP]);

  const handleZoomProcessedIn = useCallback(() => {
    zoomChart(processedChartInstance, ZOOM_STEP);
  }, [zoomChart, ZOOM_STEP]);

  const handleZoomProcessedOut = useCallback(() => {
    zoomChart(processedChartInstance, 1 / ZOOM_STEP);
  }, [zoomChart, ZOOM_STEP]);

  return (
    <div className="charts-wrapper">
      <section className="chart-section">
        <div className="chart-toolbar">
          <div className="chart-toolbar__text">
            <span className="chart-chip">Исходные сигналы</span>
            <p>Интерферограмма до обработки</p>
          </div>
          <div className="chart-toolbar__actions">
            <div className="zoom-toggles" role="group" aria-label="Изменение масштаба">
              <button
                type="button"
                className="zoom-icon-btn"
                onClick={handleZoomOriginalOut}
                aria-label="Уменьшить масштаб исходных данных"
              >
                -
              </button>
              <button
                type="button"
                className="zoom-icon-btn"
                onClick={handleZoomOriginalIn}
                aria-label="Увеличить масштаб исходных данных"
              >
                +
              </button>
            </div>
            <button
              type="button"
              className="zoom-btn"
              onClick={handleResetOriginal}
            >
              <span className="zoom-btn__icon" aria-hidden="true">
                ↺
              </span>
              <span>Сбросить масштаб</span>
            </button>
          </div>
        </div>
        <canvas ref={originalChartRef} width="800" height="400"></canvas>
      </section>

      <section className="chart-section">
        <div className="chart-toolbar">
          <div className="chart-toolbar__text">
            <span className="chart-chip chart-chip--accent">
              Обработанные сигналы
            </span>
            <p>Интерферограмма после фильтрации</p>
          </div>
          <div className="chart-toolbar__actions">
            <div className="zoom-toggles" role="group" aria-label="Изменение масштаба">
              <button
                type="button"
                className="zoom-icon-btn"
                onClick={handleZoomProcessedOut}
                aria-label="Уменьшить масштаб обработанных данных"
              >
                -
              </button>
              <button
                type="button"
                className="zoom-icon-btn"
                onClick={handleZoomProcessedIn}
                aria-label="Увеличить масштаб обработанных данных"
              >
                +
              </button>
            </div>
            <button
              type="button"
              className="zoom-btn"
              onClick={handleResetProcessed}
            >
              <span className="zoom-btn__icon" aria-hidden="true">
                ↺
              </span>
              <span>Сбросить масштаб</span>
            </button>
          </div>
        </div>
        <canvas ref={processedChartRef} width="800" height="400"></canvas>
      </section>
      {intersections.length > 0 && (
        <aside className="intersection-panel">
          <div className="intersection-panel__header">
            <span className="chart-chip chart-chip--highlight">
              Пересечения Y=0
            </span>
            <p>Общие точки tензо и интерферосигнала</p>
          </div>
          <div className="intersection-table-wrapper">
            <table className="intersection-table">
              <thead>
                <tr>
                  <th>#</th>
                  <th>Время (с)</th>
                  <th>Амплитуда (В)</th>
                </tr>
              </thead>
              <tbody>
                {intersections.map((point, idx) => (
                  <tr key={`${point.time}-${idx}`}>
                    <td>{idx + 1}</td>
                    <td>{formatTime(point.time)}</td>
                    <td>{formatSignal(point.value)}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </aside>
      )}
    </div>
  );
};

export default ChartComponent;
