const multiplyMatrices = (A, B) => {
  const aRows = A.length,
    aCols = A[0].length,
    bCols = B[0].length;
  const C = Array.from({
    length: aRows
  }, () => Array(bCols).fill(0));
  for (let i = 0; i < aRows; i++) {
    for (let k = 0; k < aCols; k++) {
      const v = A[i][k];
      for (let j = 0; j < bCols; j++) {
        C[i][j] += v * B[k][j];
      }
    }
  }
  return C;
};
