    const line = lines[i].trim();
    if (!line) continue;
    if (line.startsWith("TIME,CH1,")) {
      dataStarted = true;
      continue;
    }
    if (dataStarted) {
      lineCount++;
      if (lineCount < skipStart) continue;
      if (lineCount > skipEnd) break;
