// Полный обновлённый файл — Savitzky-Golay (без внешних библиотек), baseline removal,
// сглаживание, поиск нулевых пересечений, фильтрация близких точек и подготовка данных для графиков.

export const processCSVData = (file) => {
  return new Promise((resolve, reject) => {
    const reader = new FileReader();

    reader.onload = (e) => {
      try {
        const csvText = e.target.result;
        const results = parseCSV(csvText);
        resolve(results);
      } catch (error) {
        reject(error);
      }
    };

    reader.onerror = () => reject(new Error("Ошибка чтения файла"));
    reader.readAsText(file);
  });
