// -------------------- Утилиты для матриц (используются для SG) --------------------
const transpose = (M) => M[0].map((_, i) => M.map(row => row[i]));

const multiplyMatrices = (A, B) => {
  const aRows = A.length, aCols = A[0].length, bCols = B[0].length;
  const C = Array.from({ length: aRows }, () => Array(bCols).fill(0));
  for (let i = 0; i < aRows; i++) {
    for (let k = 0; k < aCols; k++) {
      const v = A[i][k];
      for (let j = 0; j < bCols; j++) {
        C[i][j] += v * B[k][j];
      }
    }
  }
  return C;
};

const invertMatrix = (m) => {
  const n = m.length;
  const A = m.map((row, i) => row.concat(Array.from({ length: n }, (_, j) => (i === j ? 1 : 0))));
  for (let i = 0; i < n; i++) {
    let maxRow = i;
    for (let k = i + 1; k < n; k++) if (Math.abs(A[k][i]) > Math.abs(A[maxRow][i])) maxRow = k;
    if (Math.abs(A[maxRow][i]) < 1e-15) throw new Error("Матрица вырождена, невозможно инвертировать");
    if (maxRow !== i) [A[i], A[maxRow]] = [A[maxRow], A[i]];
    const diag = A[i][i];
    for (let j = 0; j < 2 * n; j++) A[i][j] /= diag;
    for (let k = 0; k < n; k++) {
      if (k === i) continue;
      const factor = A[k][i];
      if (factor === 0) continue;
      for (let j = 0; j < 2 * n; j++) A[k][j] -= factor * A[i][j];
    }
  }
  return A.map(row => row.slice(n));
};

// -------------------- Savitzky–Golay фильтр --------------------
const savitzkyGolay = (data, windowSize = 31, polyOrder = 3) => {
  if (!Array.isArray(data) || data.length === 0) return [];
  windowSize = Math.max(3, Math.floor(windowSize));
  if (windowSize % 2 === 0) windowSize += 1;
  polyOrder = Math.max(1, Math.floor(polyOrder));
  if (polyOrder >= windowSize) polyOrder = windowSize - 1;

  const n = data.length;
  if (n <= windowSize) {
    const out = new Array(n).fill(0);
    const half = Math.floor(windowSize / 2);
    for (let i = 0; i < n; i++) {
      let sum = 0, cnt = 0;
      for (let j = i - half; j <= i + half; j++) {
        const idx = Math.min(n - 1, Math.max(0, j));
        sum += data[idx]; cnt++;
      }
      out[i] = sum / cnt;
    }
    return out;
  }

  const half = Math.floor(windowSize / 2);
  const A = [];
  for (let i = -half; i <= half; i++) {
    const row = [];
    for (let p = 0; p <= polyOrder; p++) row.push(Math.pow(i, p));
    A.push(row);
  }

  const AT = transpose(A);
  const ATA = multiplyMatrices(AT, A);
  let invATA;
  try { invATA = invertMatrix(ATA); } catch (err) { return data.slice(); }
  const ATAinvAT = multiplyMatrices(invATA, AT);
  const coeffs = ATAinvAT[0];

  const out = new Array(n).fill(0);
  for (let i = 0; i < n; i++) {
    let acc = 0;
    for (let j = -half; j <= half; j++) {
      let idx = i + j;
      if (idx < 0) idx = 0;
      if (idx >= n) idx = n - 1;
      acc += data[idx] * coeffs[j + half];
    }
    out[i] = acc;
  }

  return out;
};

// -------------------- Baseline removal --------------------
const removeBaseline = (arr, N = 2000) => {
  if (!Array.isArray(arr) || arr.length === 0) return [];
  const count = Math.min(arr.length, N);
  const avg = arr.slice(0, count).reduce((s, v) => s + v, 0) / count;
  return arr.map(v => v - avg);
};

// -------------------- Парсинг CSV --------------------
const parseCSV = (csvText, options = {}) => {
  const sgWindow = options.sgWindow || 31;
  const sgPoly = options.sgPoly || 3;
  const baselineN = options.baselineN || 2000;
  const skipStart = options.skipStart || 6650;
  const skipEnd = options.skipEnd || 27000;

  const lines = csvText.split("\n");
  const t = [], tenz = [], interf = [];
  let dataStarted = false, lineCount = 0;

  for (let i = 0; i < lines.length; i++) {
    const line = lines[i].trim();
    if (!line) continue;
    if (line.startsWith("TIME,CH1,")) { dataStarted = true; continue; }
    if (dataStarted) {
      lineCount++;
      if (lineCount < skipStart) continue;
      if (lineCount > skipEnd) break;

      const fields = line.split(",");
      if (fields.length >= 5) {
        const timeVal = parseFloat(fields[0]);
        const ch1Val = parseFloat(fields[1]);
        const ch3Val = parseFloat(fields[3]);
        if (!isNaN(timeVal) && !isNaN(ch1Val) && !isNaN(ch3Val)) {
          t.push(timeVal);
          tenz.push(ch1Val * 0.00132 * 1.25);
          interf.push(ch3Val + 0.04);
        }
      }
    }
  }

  const interfSmoothed = savitzkyGolay(interf, sgWindow, sgPoly);
  const interfCorrected = removeBaseline(interfSmoothed, baselineN);

  const { t0, y0 } = findZeroCrossings(t, interfCorrected);
  const { filteredT0, filteredY0 } = filterClosePoints(t0, y0, 100e-9);

  const dudt_interf = calculateDerivativeSmoothed(interfCorrected, sgWindow, sgPoly, t);

  const intersectionPoints = findSignalIntersectionsAtZero(t, tenz, interfCorrected);

  return { t, tenz, interf, interfSmoothed, interfCorrected, t0: filteredT0, y0: filteredY0, dudt_interf, intersectionPoints };
};

// -------------------- Сглаженная производная --------------------
const calculateDerivativeSmoothed = (signal, windowSize = 11, polyOrder = 3, t = []) => {
  const n = signal.length;
  if (n < 2) return [];

  // Вычисляем численную производную
  const dtArr = [];
  for (let i = 1; i < n; i++) dtArr.push(t[i] - t[i - 1]);
  const derivative = [];
  derivative.push(0);
  for (let i = 1; i < n; i++) {
    derivative.push((signal[i] - signal[i - 1]) / (dtArr[i - 1] || 1));
  }

  // Сглаживаем производную фильтром Савицкого–Голея
  return savitzkyGolay(derivative, windowSize, polyOrder);
};

// -------------------- Нулевые пересечения --------------------
const findZeroCrossings = (t, interf, eps = 1e-6) => {
  const t0 = [], y0 = [];
  if (!t || !interf) return { t0, y0 };
  for (let i = 1; i < interf.length; i++) {
    const y1 = interf[i - 1], y2 = interf[i];
    const x1 = t[i - 1], x2 = t[i];
    if (Math.abs(y1) < eps) { t0.push(x1); y0.push(0); continue; }
    if (Math.abs(y2) < eps) { t0.push(x2); y0.push(0); continue; }
    if (y1 * y2 < 0) {
      const t_zero = x1 - y1 * (x2 - x1) / (y2 - y1);
      t0.push(t_zero); y0.push(0);
    }
  }
  return { t0, y0 };
};

// -------------------- Фильтрация близких точек --------------------
const filterClosePoints = (t0, y0, minDistance) => {
  if (!Array.isArray(t0) || t0.length <= 1) return { filteredT0: t0 || [], filteredY0: y0 || [] };
  let filteredT0 = [...t0], filteredY0 = [...y0];
  for (let k = 0; k < 10; k++) {
    const newT0 = [filteredT0[0]], newY0 = [filteredY0[0]];
    for (let i = 1; i < filteredT0.length; i++) {
      if (filteredT0[i] - newT0[newT0.length - 1] >= minDistance) {
        newT0.push(filteredT0[i]); newY0.push(filteredY0[i]);
      }
    }
    if (newT0.length === filteredT0.length) break;
    filteredT0 = newT0; filteredY0 = newY0;
  }
  return { filteredT0, filteredY0 };
};

// -------------------- Поиск пересечений сигналов --------------------
const findSignalIntersectionsAtZero = (t, tenz, interf, yThreshold = 0.02) => {
  const intersections = [];
  if (!t || !tenz || !interf) return intersections;
  for (let i = 1; i < t.length; i++) {
    const diffPrev = tenz[i - 1] - interf[i - 1];
    const diffCurr = tenz[i] - interf[i];
    if ((diffPrev <= 0 && diffCurr > 0) || (diffPrev >= 0 && diffCurr < 0)) {
      const t1 = t[i - 1], t2 = t[i], diffDelta = diffCurr - diffPrev;
      if (diffDelta === 0) continue;
      const ratio = -diffPrev / diffDelta;
      const time = t1 + ratio * (t2 - t1);
      const tenzValue = tenz[i - 1] + ratio * (tenz[i] - tenz[i - 1]);
      const interfValue = interf[i - 1] + ratio * (interf[i] - interf[i - 1]);
      const value = (tenzValue + interfValue) / 2;
      if (Math.abs(value) <= yThreshold) intersections.push({ time, value });
    }
  }
  return intersections;
};

// -------------------- Подготовка данных для графиков --------------------
export const generateChartData = (results) => {
  const { t, tenz, interf, interfSmoothed, interfCorrected, dudt_interf, intersectionPoints } = results;
  const step = Math.max(1, Math.floor(t.length / 2000));
  const limitedT = [], limitedTenz = [], limitedInterf = [], limitedInterfSmoothed = [];
  for (let i = 0; i < t.length; i += step) {
    limitedT.push(t[i]); limitedTenz.push(tenz[i]);
    limitedInterf.push(interfCorrected[i]); limitedInterfSmoothed.push(interfSmoothed[i]);
  }

  const chartData = {
    original: [
      { label: "Тензометрический сигнал (CH1)", data: limitedT.map((time, idx) => ({ x: time, y: limitedTenz[idx] })), borderColor: "rgb(255, 99, 132)", backgroundColor: "rgba(255, 99, 132, 0.1)", borderWidth: 1, tension: 0.1 },
      { label: "Интерферометрический сигнал (CH3, сглаженный SG)", data: limitedT.map((time, idx) => ({ x: time, y: limitedInterfSmoothed[idx] })), borderColor: "rgb(0,0,0)", backgroundColor: "rgba(0,0,0,0.05)", borderWidth: 1, tension: 0.1 },
      { label: "Интерферометрический сигнал (CH3, центрированный)", data: limitedT.map((time, idx) => ({ x: time, y: limitedInterf[idx] })), borderColor: "rgb(54,162,235)", backgroundColor: "rgba(54,162,235,0.1)", borderWidth: 1, tension: 0.1 }
    ],
    processed: [
      { label: "Производная интерферометрического сигнала", data: limitedT.map((time, idx) => ({ x: time, y: dudt_interf[idx] })), borderColor: "rgb(75,192,192)", backgroundColor: "rgba(75,192,192,0.1)", borderWidth: 2, pointRadius: 0, showLine: true },
      { label: "Скорректированный тензосигнал", data: limitedT.map((time, idx) => ({ x: time, y: -limitedTenz[idx] * 5000 + 0.38 })), borderColor: "rgb(153,102,255)", backgroundColor: "rgba(153,102,255,0.1)", borderWidth: 1, tension: 0.1 }
    ]
  };

  if (intersectionPoints && intersectionPoints.length) {
    chartData.original.push({
      label: "Пересечения при Y=0",
      data: intersectionPoints.map(({ time, value }) => ({ x: time, y: value })),
      showLine: false, pointStyle: "crossRot", pointRadius: 3, pointHoverRadius: 4, borderWidth: 0, order: 99, z: 50,
      intersectionMarker: true, markerSize: 4, markerColor: "#000", markerAlpha: 0.7, markerLineWidth: 2
    });
  }

  chartData.intersections = intersectionPoints || [];
  chartData.rawData = { t: limitedT, tenz: limitedTenz, interf: limitedInterf, fullT: t, fullTenz: tenz, fullInterf: interfCorrected, dudt_interf };

  return chartData;
};
