import React, { useEffect, useRef } from "react";
import { Chart, registerables } from "chart.js";

Chart.register(...registerables);

const ChartComponent = ({ data }) => {
  const originalChartRef = useRef(null);
  const processedChartRef = useRef(null);

  useEffect(() => {
    if (!data) return;

    let originalChart, processedChart;

    // График исходных данных
    if (originalChartRef.current) {
      const ctx = originalChartRef.current.getContext("2d");
      originalChart = new Chart(ctx, {
        type: "line",
        data: {
          datasets: data.original,
        },
        options: {
          responsive: true,
          interaction: {
            mode: "index",
            intersect: false,
          },
          scales: {
            x: {
              type: "linear",
              title: {
                display: true,
                text: "Время (секунды)",
              },
            },
            y: {
              type: "linear",
              title: {
                display: true,
                text: "Сигнал (В)",
              },
            },
          },
          plugins: {
            title: {
              display: true,
              text: "Исходные сигналы",
            },
            legend: {
              display: true,
            },
          },
        },
      });
    }

    // График обработанных данных
    if (processedChartRef.current) {
      const ctx = processedChartRef.current.getContext("2d");
      processedChart = new Chart(ctx, {
        type: "line",
        data: {
          datasets: data.processed,
        },
        options: {
          responsive: true,
          interaction: {
            mode: "index",
            intersect: false,
          },
          scales: {
            x: {
              type: "linear",
              title: {
                display: true,
                text: "Время (секунды)",
              },
            },
            y: {
              type: "linear",
              title: {
                display: true,
                text: "Сигнал",
              },
            },
          },
          plugins: {
            title: {
              display: true,
              text: "Обработанные сигналы",
            },
            legend: {
              display: true,
            },
          },
        },
      });
    }

    return () => {
      if (originalChart) originalChart.destroy();
      if (processedChart) processedChart.destroy();
    };
  }, [data]);

  return (
    <div className="charts-wrapper">
      <div className="chart-section">
        <canvas ref={originalChartRef} width="800" height="400"></canvas>
      </div>

      <div className="chart-section">
        <canvas ref={processedChartRef} width="800" height="400"></canvas>
      </div>
    </div>
  );
};

export default ChartComponent;
