import React, { useEffect, useRef } from "react";
import { Chart, registerables } from "chart.js";

Chart.register(...registerables);

const ChartComponent = ({ data }) => {
  const originalChartRef = useRef(null);
  const processedChartRef = useRef(null);

  useEffect(() => {
    // График исходных данных
    const originalCtx = originalChartRef.current.getContext("2d");
    const originalChart = new Chart(originalCtx, {
      type: "line",
      data: {
        datasets: [
          {
            label: "Тензометрический сигнал",
            data: data.original.t.map((t, i) => ({
              x: t,
              y: data.original.tenz[i],
            })),
            borderColor: "rgb(255, 99, 132)",
            backgroundColor: "rgba(255, 99, 132, 0.1)",
            borderWidth: 1,
            tension: 0.1,
          },
          {
            label: "Интерферометрический сигнал",
            data: data.original.t.map((t, i) => ({
              x: t,
              y: data.original.interf[i],
            })),
            borderColor: "rgb(54, 162, 235)",
            backgroundColor: "rgba(54, 162, 235, 0.1)",
            borderWidth: 1,
            tension: 0.1,
          },
        ],
      },
      options: {
        responsive: true,
        interaction: {
          mode: "index",
          intersect: false,
        },
        scales: {
          x: {
            type: "linear",
            title: {
              display: true,
              text: "Время",
            },
          },
          y: {
            type: "linear",
            title: {
              display: true,
              text: "Сигнал",
            },
          },
        },
      },
    });

    // График обработанных данных
    const processedCtx = processedChartRef.current.getContext("2d");
    const processedChart = new Chart(processedCtx, {
      type: "line",
      data: {
        datasets: [
          {
            label: "Производная интерферометрического сигнала",
            data: data.processed.t0.map((t, i) => ({
              x: t,
              y: data.processed.dudt[i],
            })),
            borderColor: "rgb(75, 192, 192)",
            backgroundColor: "rgba(75, 192, 192, 0.1)",
            borderWidth: 2,
            pointRadius: 3,
            showLine: false,
          },
          {
            label: "Скорректированный тензометрический сигнал",
            data: data.processed.correctedTenz,
            borderColor: "rgb(153, 102, 255)",
            backgroundColor: "rgba(153, 102, 255, 0.1)",
            borderWidth: 1,
            tension: 0.1,
          },
        ],
      },
      options: {
        responsive: true,
        interaction: {
          mode: "index",
          intersect: false,
        },
        scales: {
          x: {
            type: "linear",
            title: {
              display: true,
              text: "Время",
            },
          },
          y: {
            type: "linear",
            title: {
              display: true,
              text: "Сигнал",
            },
          },
        },
      },
    });

    return () => {
      originalChart.destroy();
      processedChart.destroy();
    };
  }, [data]);

  return (
    <div className="charts-wrapper">
      <div className="chart-section">
        <h3>Исходные сигналы</h3>
        <canvas ref={originalChartRef} width="800" height="400"></canvas>
      </div>

      <div className="chart-section">
        <h3>Обработанные сигналы</h3>
        <canvas ref={processedChartRef} width="800" height="400"></canvas>
      </div>
    </div>
  );
};

export default ChartComponent;
