import React, { useEffect, useRef, useState, useMemo, useCallback } from "react";
import { Chart, registerables } from "chart.js";
import zoomPlugin from "chartjs-plugin-zoom";

import {
  findSignalIntersectionsAtZero
} from "./csvProcessor"; // импорт функции пересечений, остальные уже используются через props

Chart.register(...registerables, zoomPlugin);

const ZOOM_STEP = 1.25;

const ChartComponent = ({ data }) => {
  const originalChartRef = useRef(null);
  const processedChartRef = useRef(null);
  const originalChartInstance = useRef(null);
  const processedChartInstance = useRef(null);

  const [tenzOffset, setTenzOffset] = useState(0);
  const [currentIntersections, setCurrentIntersections] = useState([]);

  // диапазон сдвига
  const offsetRange = useMemo(() => {
    if (!data?.tenz) return { min: -0.1, max: 0.1, step: 0.001 };
    const range = Math.max(...data.tenz) - Math.min(...data.tenz);
    return { min: -range, max: range, step: range / 1000 };
  }, [data?.tenz]);

  // среднее Y для пересечений
  const averageY = useMemo(() => {
    if (!currentIntersections.length) return 0;
    const sum = currentIntersections.reduce((acc, p) => acc + p.value, 0);
    return sum / currentIntersections.length;
  }, [currentIntersections]);

  useEffect(() => {
    if (!data) return;

    // очищаем старые графики
    if (originalChartInstance.current) originalChartInstance.current.destroy();
    if (processedChartInstance.current) processedChartInstance.current.destroy();

    const buildOptions = (title, yLabel) => ({
      responsive: true,
      interaction: { mode: "index", intersect: false },
      scales: {
        x: { type: "linear", title: { display: true, text: "Время (сек)" } },
        y: { type: "linear", title: { display: true, text: yLabel } }
      },
      plugins: { 
        legend: { display: true },
        title: { display: true, text: title },
        zoom: {
          pan: { enabled: true, mode: "xy" },
          zoom: { wheel: { enabled: true, modifierKey: "ctrl" }, pinch: { enabled: true }, mode: "xy" }
        }
      }
    });

    const ctxOrig = originalChartRef.current.getContext("2d");
    originalChartInstance.current = new Chart(ctxOrig, {
      type: "line",
      data: {
        datasets: [
          { label: "Тензосигнал", data: data.t.map((x,i)=>({x,y:data.tenz[i]})), borderColor:"rgb(255,99,132)" },
          { label: "Интерф CH3 (SG)", data: data.t.map((x,i)=>({x,y:data.interfSmoothed[i]})), borderColor:"rgb(0,0,0)" },
          { label: "Интерф центрированный", data: data.t.map((x,i)=>({x,y:data.interfCorrected[i]})), borderColor:"rgb(54,162,235)" },
        ]
      },
      options: buildOptions("Исходные сигналы", "Сигнал")
    });

    const ctxProc = processedChartRef.current.getContext("2d");
    processedChartInstance.current = new Chart(ctxProc, {
      type: "line",
      data: {
        datasets: [
          { label: "Производная (локальная)", data: data.t.map((x,i)=>({x,y:data.dudt_interf[i]})), borderColor:"rgb(75,192,192)", borderWidth:2, pointRadius:0 },
          { label: `Пересечения Y=${averageY.toFixed(4)}`, data: data.intersectionPoints.map(p=>({x:p.time,y:p.value})), pointStyle:"crossRot", pointRadius:4, showLine:false, borderColor:"rgb(255,0,0)" }
        ]
      },
      options: buildOptions("Обработанные сигналы", "Сигнал")
    });

    setCurrentIntersections(data.intersectionPoints || []);

  }, [data, averageY]);

  // обновление графика при сдвиге
  useEffect(() => {
    if (!data || !originalChartInstance.current || !processedChartInstance.current) return;

    const shiftedTenz = data.tenz.map(v => v + tenzOffset);
    const intersections = findSignalIntersectionsAtZero(data.t, shiftedTenz, data.interfCorrected);

    // обновляем исходный график
    const origChart = originalChartInstance.current;
    origChart.data.datasets[0].data = data.t.map((x,i)=>({x,y:shiftedTenz[i]}));
    origChart.update("none");

    // обновляем пересечения на обработанном графике
    const procChart = processedChartInstance.current;
    procChart.data.datasets[1].data = intersections.map(p=>({x:p.time,y:p.value}));
    procChart.data.datasets[1].label = `Пересечения Y=${averageY.toFixed(4)}`;
    procChart.update("none");

    setCurrentIntersections(intersections);
  }, [tenzOffset, data, averageY]);

  return (
    <div className="charts-wrapper">
      {data?.t && (
        <div className="tenz-offset-control">
          <label>Сдвиг тензосигнала (CH1) по Y:</label>
          <div className="tenz-offset-input-group">
            <input type="range" min={offsetRange.min} max={offsetRange.max} step={offsetRange.step} value={tenzOffset} 
              onChange={e=>setTenzOffset(parseFloat(e.target.value))} />
            <input type="number" min={offsetRange.min} max={offsetRange.max} step={offsetRange.step} value={tenzOffset} 
              onChange={e=>setTenzOffset(parseFloat(e.target.value) || 0)} />
            <button onClick={()=>setTenzOffset(0)}>Сбросить</button>
          </div>
        </div>
      )}
      <canvas ref={originalChartRef} width="800" height="400"></canvas>
      <canvas ref={processedChartRef} width="800" height="400"></canvas>
    </div>
  );
};

export default ChartComponent;
