    if (i === 0) u_interf.push(0);
    else {
      const du = 0.63 / 4e6; // единичный шаг на полупериод
      u_interf.push(u_interf[i - 1] + du);
      const dt = t0[i] - t0[i - 1] || 1e-12;
      dudt_interf.push(du / dt);
    }
  }
  dudt_interf.unshift(dudt_interf[0] || 0); // чтобы совпало с длиной

  const intersectionPoints = findSignalIntersectionsAtZero(t, tenz, interfCorrected);

  return { t, tenz, interf, interfSmoothed, interfCorrected, t0, y0, u_interf, dudt_interf, intersectionPoints };
};

// -------------------- Подготовка графика --------------------
export const generateChartData = (results, options = {}) => {
  const { t, tenz, interf, interfSmoothed, interfCorrected, u_interf, dudt_interf, intersectionPoints } = results;
  const step = Math.max(1, Math.floor(t.length / (options.maxPoints || 2000)));

  const limitedT = [], limitedTenz = [], limitedInterf = [], limitedInterfSmoothed = [], limitedDudt = [];
  for (let i = 0; i < t.length; i += step) {
    limitedT.push(t[i]);
    limitedTenz.push(tenz[i]);
    limitedInterf.push(interfCorrected[i]);
    limitedInterfSmoothed.push(interfSmoothed[i]);
    limitedDudt.push(dudt_interf[i]);
  }

  // Масштабирование производной под тензосигнал
  const tenzMin = Math.min(...limitedTenz);
  const tenzMax = Math.max(...limitedTenz);
  const dudtMin = Math.min(...limitedDudt);
  const dudtMax = Math.max(...limitedDudt);
  const scale = (tenzMax - tenzMin) / (dudtMax - dudtMin || 1);
  const dudtScaled = limitedDudt.map(v => tenzMin + (v - dudtMin) * scale);

  const intersectionDerivative = (intersectionPoints || []).map(({ time, value }) => ({
    x: time,
    y: tenzMin + (value - dudtMin) * scale
  }));

  const chartData = {
    original: [
      { label: "Тензосигнал", data: limitedT.map((x, i) => ({ x, y: limitedTenz[i] })), borderColor: "rgb(255,99,132)", backgroundColor: "rgba(255,99,132,0.1)", borderWidth: 1, tension: 0.1 },
      { label: "Интерферометрический сигнал (центрированный)", data: limitedT.map((x, i) => ({ x, y: limitedInterf[i] })), borderColor: "rgb(54,162,235)", backgroundColor: "rgba(54,162,235,0.1)", borderWidth: 1, tension: 0.1 },
    ],
    processed: [
      { label: "Интегральное u_interf", data: t0.map((x,i) => ({ x, y: u_interf[i] })), borderColor: "rgb(75,192,192)", borderWidth: 2, pointRadius: 2, showLine: true },
      { label: "Производная dudt_interf (масштаб под тензосигнал)", data: limitedT.map((x,i) => ({ x, y: dudtScaled[i] })), borderColor: "rgb(75,192,192)", backgroundColor: "rgba(75,192,192,0.1)", borderWidth: 2, pointRadius: 0, showLine: true },
      { label: "Пересечения", data: intersectionDerivative, pointStyle: "crossRot", pointRadius: 4, showLine: false, borderColor: "rgb(75,192,192)" },
    ]
  };

  return chartData;
};
