  if (windowSize % 2 === 0) windowSize += 1;
  polyOrder = Math.max(1, Math.floor(polyOrder));
  if (polyOrder >= windowSize) polyOrder = windowSize - 1;
  const n = data.length;
  if (n <= windowSize) {
    const out = new Array(n).fill(0);
    const half = Math.floor(windowSize / 2);
    for (let i = 0; i < n; i++) {
      let sum = 0,
        cnt = 0;
      for (let j = i - half; j <= i + half; j++) {
        const idx = Math.min(n - 1, Math.max(0, j));
        sum += data[idx];
        cnt++;
      }
      out[i] = sum / cnt;
    }
    return out;
  }
  const half = Math.floor(windowSize / 2);
  const A = [];
  for (let i = -half; i <= half; i++) {
    const row = [];
    for (let p = 0; p <= polyOrder; p++) row.push(Math.pow(i, p));
    A.push(row);
  }
  const AT = transpose(A);
  const ATA = multiplyMatrices(AT, A);
  let invATA;
  try {
    invATA = invertMatrix(ATA);
  } catch {
    return data.slice();
  }
  const ATAinvAT = multiplyMatrices(invATA, AT);
  const coeffs = ATAinvAT[0];
  const out = new Array(n).fill(0);
  for (let i = 0; i < n; i++) {
    let acc = 0;
    for (let j = -half; j <= half; j++) {
      let idx = i + j;
      if (idx < 0) idx = 0;
      if (idx >= n) idx = n - 1;
      acc += data[idx] * coeffs[j + half];
    }
    out[i] = acc;
  }
  return out;
};

// -------------------- SG для первой производной (ИСПРАВЛЕННАЯ) --------------------
